import React from "react";
import './sign-in.styles.scss';
import FormInput from "../form-input/form-input.component";

class SignIn extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            email : '',
            password: ''
        }
    }

    handleSubmit = event => {
        event.preventDefault();

        this.setState({email:'',password:''})
    }

    handleChange = event =>{
        const { value, name} = event.target;

        this.setState({[name]:value})
    }






    render(){
        return(
            <div className="sign-in">
                <h2>I already have an account</h2>
                <span>Sign in with your email and password</span>
                <form onSubmit={this.handleSubmit}>
                    <FormInput type='email' name="email" value={this.state.email} required handleChange={this.handleChange} label="E"/>
                    {/* <label>Email</label> */}
                    <FormInput type="password" name="email" value={this.state.email} required handleChange={this.handleChange} label={Password}/>
                    {/* <label>Password</label> */}
                    <input type="submit" value='Submit Form' />
                </form>
            </div>
        )
    }
}


export default SignIn;
