    return (
      <div>
        <Router>
          <Header />
          <Switch>
            <Route exact path="/" component={Homepage} />
            <Route path="/shop" component={ ShopPage } />
            <Route path="/signin" component={ SignInAndSignOut } />
          </Switch>
        </Router>     

      </div>
    );

  }
}

export default App;
